( function ( $ ) {
	/**
	 * Class Frontend Main JS.
	 */
	class FUTUREWORDPRESS_PROJECT_SEOJAWS_FROTEND {
		/**
		 * Contructor.
		 */
		constructor() {
			this.init();
		}
		init() {
      this.remove();
      this.model();
    }
    remove() {
      document.querySelectorAll( '.fwp-body.fwp-single-product-hide .ppc-button-wrapper, .fwp-body.fwp-single-product-hide form.cart' ).forEach( function( e, i ) {
        e.remove();
      } );
    }
    model() {
      document.querySelectorAll( '.sign_up_modal .sign_up_tab' ).forEach( function( tabs, i ) {
        tabs.querySelectorAll( 'li a' ).forEach( function( tab, ti ) {
          tab.addEventListener( 'click', function( e ) {
            e.preventDefault();
            this.classList.add( 'active' );
            var a = ( this.parentNode.nextElementSibling ) ? this.parentNode.nextElementSibling : ( ( this.parentNode.previousElementSibling ) ? this.parentNode.previousElementSibling : false );
            if( ! a ) {return;}
            a.querySelector( 'a' ).classList.remove( 'active' );
            var tab = document.querySelector( this.getAttribute( 'href' ) );
            tab.parentNode.querySelectorAll( '.tab-pane' ).forEach( function( tc, tci ) {
              tc.classList.remove( 'show', 'active' );
            } );
            tab.classList.add( 'show', 'active' );
          } );
        } );
      } );
    }
	}

	new FUTUREWORDPRESS_PROJECT_SEOJAWS_FROTEND();
} )( jQuery );